import React from "react";
import {
  Box,
  styled,
  FormControl,
  InputBase,
  Button,
  TextareaAutosize,
} from "@mui/material";
import { AddToPhotos as Add } from "@mui/icons-material";

const Container = styled(Box)`
  margin: 50px 100px;
`;
const Image = styled("img")({
  width: "100%",
  height: "50vh",
  objectFit: "cover",
});

const StyledFormControl = styled(FormControl)`
  margin-top: 10px;
  display: flex;
  flex-direction: row;
`

const InputTextField = styled(InputBase)`
  flex: 1;
  margin: 0 30px;
  font-sie: 25px;
`

const TextArea = styled(TextareaAutosize)`
  width: 100%;
  margin-top: 50px;
  font-size: 18px;
  border: none;
  &:focus-visible {
    outline: none;
  }
` 

const initialPost = {
    title:'',
    description:'',
    picture:'',
    username:'',
    categories:'',
    createdDate:new Date()
}


const CreatePost = () => {
  const ImageUrl =
    "https://www.bannerflow.com/app/uploads/blog-header-creative-banner.jpg";





  return (
    <Container>
      <Image src={ImageUrl} alt="banner" />

      <StyledFormControl>
        <label htmlFor="fileInput">
          <Add fontSize="large" color="action" />
        </label>
        <input type="file" id="fileInput" style={{ display: "none" }} />
        <InputTextField placeholder="Title" />
        <Button variant="contained">Publish</Button>
      </StyledFormControl>

      <TextArea minRows={5} placeholder="Tell your Story..." />
    </Container>
  );
};

export default CreatePost;
