import React from "react";
import {
  Button,
  TableCell,
  TableRow,
  Table,
  TableHead,
  TableBody,
  styled
} from "@mui/material";
import {Link, useSearchParams} from 'react-router-dom';
import { categories } from "../../constants/data";


const StyledTable= styled(Table)`
border : 1px solid rgba(224,224,224,1);
`
const StyledButton = styled(Button)`

margin:20px;
width:85%;
background:#6495ED; 
color:#fff;
`

const StyledLink = styled(Link)`
text-decoration:none;
color:inherit;
`

const Categories = () => {

  const [searchParams] = useSearchParams();
  const category = searchParams.get('category');

  return (
    <>
     <StyledLink to={`/create?category=${category||''}`} style={{textDecoration: 'none'}}> <StyledButton variant="contained">Create Blog</StyledButton></StyledLink>
      <StyledTable>
        <TableHead>
          <TableRow>
            <TableCell>
              <Link to={'/'}>
              All Categories
              </Link>
             </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {categories.map((category) => {
            return (
              <TableRow key={category.id}>

                <TableCell>
                  <StyeLink to={`/?category=${category.type}`}>
                  {category.type}
                  </StyeLink>
                  </TableCell>
              </TableRow>
            );
          })}
        </TableBody>
      </StyledTable>
    </>
  );
};

export default Categories;
