import React from 'react'
import { Box,styled,FormControl,Input } from '@mui/material'
import {AddToPhotos as Add} from '@mui/icons-material';

const Container = styled(Box)`
margin:50px 100px;
`


const Image = styled('img')({
    width:'100%',
    height:'50vh',
    objectFit:'cover'
})


const CreatePost = () => {
    const ImageUrl ='https://www.bannerflow.com/app/uploads/blog-header-creative-banner.jpg';


  return (
    <Container>
        <Image src={ImageUrl} alt='banner'/>

        <FormControl>
            <label htmlFor='fileInput'>
                <Add fontSize='large' color='action'/>
            </label>
            <input type='file' 
            id='fileInput'
            style={{display:'none'}}
            />
        </FormControl>

        </Container>
  )
}

export default CreatePost
